@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 220 15% 10%;

  --border: 220 13% 89%;

  --card: 0 0% 100%;

  --card-foreground: 220 15% 10%;

  --card-border: 220 13% 92%;

  --sidebar: 220 14% 97%;

  --sidebar-foreground: 220 15% 10%;

  --sidebar-border: 220 13% 90%;

  --sidebar-primary: 210 80% 35%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 220 14% 94%;

  --sidebar-accent-foreground: 220 15% 10%;

  --sidebar-ring: 210 80% 35%;

  --popover: 0 0% 100%;

  --popover-foreground: 220 15% 10%;

  --popover-border: 220 13% 88%;

  --primary: 210 80% 35%;

  --primary-foreground: 0 0% 100%;

  --secondary: 220 14% 92%;

  --secondary-foreground: 220 15% 10%;

  --muted: 220 12% 95%;

  --muted-foreground: 220 9% 45%;

  --accent: 210 80% 96%;

  --accent-foreground: 210 80% 35%;

  --destructive: 0 72% 51%;

  --destructive-foreground: 0 0% 100%;

  --input: 220 13% 90%;
  --ring: 210 80% 35%;
  --chart-1: 210 80% 35%;
  --chart-2: 25 70% 50%;
  --chart-3: 142 71% 45%;
  --chart-4: 199 89% 48%;
  --chart-5: 38 92% 50%;

  --font-sans: Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, 'Times New Roman', serif;
  --font-mono: 'Fira Code', Menlo, Monaco, monospace;
  --radius: 0.5rem; /* 8px */
  --shadow-2xs: 0px 1px 2px 0px rgba(0, 0, 0, 0.03);
  --shadow-xs: 0px 1px 3px 0px rgba(0, 0, 0, 0.04);
  --shadow-sm: 0px 1px 2px 0px rgba(0, 0, 0, 0.05), 0px 1px 3px 0px rgba(0, 0, 0, 0.06);
  --shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.07), 0px 2px 6px 0px rgba(0, 0, 0, 0.08);
  --shadow-md: 0px 2px 4px -1px rgba(0, 0, 0, 0.06), 0px 4px 8px 0px rgba(0, 0, 0, 0.10);
  --shadow-lg: 0px 4px 6px -1px rgba(0, 0, 0, 0.07), 0px 8px 14px 0px rgba(0, 0, 0, 0.12);
  --shadow-xl: 0px 6px 10px -1px rgba(0, 0, 0, 0.08), 0px 16px 24px 0px rgba(0, 0, 0, 0.15);
  --shadow-2xl: 0px 10px 20px -1px rgba(0, 0, 0, 0.10), 0px 24px 48px 0px rgba(0, 0, 0, 0.18);
  --tracking-normal: -0.011em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

