    await db
      .update(marketplaceListings)
      .set({
        status: 'approved',
        itemId,
        updatedAt: new Date(),
      })
      .where(eq(marketplaceListings.id, listingId));

    // Update verification queue
    await db
      .update(verificationQueue)
      .set({
        reviewedAt: new Date(),
        reviewedBy: reviewerId,
      })
      .where(eq(verificationQueue.listingId, listingId));
  }

